sourcesContent:
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { 
  ArrowLeft, 
  AlertTriangle, 
  Info, 
  Clock, 
  Target, 
  User, 
  Calendar, 
  CheckCircle, 
  ExternalLink,
  X,
  Bell
} from 'lucide-react';
import { motion } from 'framer-motion';

const MessageDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [alert, setAlert] = useState(null);
  const [loading, setLoading] = useState(true);

  // 模拟预警数据（与AlertNotification组件保持一致）
  const mockAlerts = [
    {
      id: 1,
      type: 'task_overdue',
      title: '任务延期预警',
      message: '任务"完成用户界面设计"将在2天后到期，当前进度75%',
      priority: 'high',
      target: 'task',
      targetId: 1,
      createdAt: new Date(Date.now() - 1000 * 60 * 30), // 30分钟前
      isRead: false,
      details: {
        taskName: '完成用户界面设计',
        projectName: '移动端应用开发',
        assignee: '张三',
        dueDate: '2024-01-22',
        currentProgress: 75,
        expectedProgress: 90,
        description: '设计并实现移动端应用的用户界面，包括登录页面、主页面、设置页面等核心功能界面。',
        recommendations: [
          '增加工作时间投入',
          '寻求设计团队协助',
          '优先完成核心功能界面'
        ]
      }
    },
    {
      id: 2,
      type: 'project_delay',
      title: '项目延期预警',
      message: '项目"移动端应用开发"进度滞后，当前进度45%，预期进度60%',
      priority: 'medium',
      target: 'project',
      targetId: 1,
      createdAt: new Date(Date.now() - 1000 * 60 * 60 * 2), // 2小时前
      isRead: false,
      details: {
        projectName: '移动端应用开发',
        department: '技术部',
        manager: '李四',
        startDate: '2024-01-01',
        endDate: '2024-03-15',
        currentProgress: 45,
        expectedProgress: 60,
        description: '开发企业移动端应用，支持iOS和Android平台，包含用户管理、数据展示、消息推送等功能。',
        riskFactors: [
          '人力资源不足',
          '技术难点较多',
          '需求变更频繁'
        ],
        recommendations: [
          '增加开发人员',
          '优化开发流程',
          '加强需求管理'
        ]
      }
    },
    {
      id: 3,
      type: 'workload_overload',
      title: '工作量超载预警',
      message: '用户"王五"当前有8个进行中任务，可能存在工作量超载',
      priority: 'medium',
      target: 'user',
      targetId: 3,
      createdAt: new Date(Date.now() - 1000 * 60 * 60 * 4), // 4小时前
      isRead: true,
      details: {
        userName: '王五',
        department: '技术部',
        position: '高级开发工程师',
        currentTasks: 8,
        maxRecommendedTasks: 5,
        taskList: [
          'API接口开发',
          '数据库设计',
          '前端页面开发',
          '单元测试编写',
          '文档编写',
          '代码审查',
          '性能优化',
          'Bug修复'
        ],
        workloadScore: 85,
        recommendations: [
          '重新分配部分任务',
          '延长任务截止时间',
          '提供技术支持协助'
        ]
      }
    },
    {
      id: 4,
      type: 'milestone_risk',
      title: '里程碑风险预警',
      message: '里程碑"第一阶段完成"可能无法按时达成，建议调整计划',
      priority: 'high',
      target: 'milestone',
      targetId: 1,
      createdAt: new Date(Date.now() - 1000 * 60 * 60 * 6), // 6小时前
      isRead: false,
      details: {
        milestoneName: '第一阶段完成',
        projectName: '移动端应用开发',
        dueDate: '2024-02-01',
        currentDate: new Date(),
        completionRate: 65,
        requiredRate: 80,
        deliverables: [
          '用户界面设计完成',
          '核心功能开发完成',
          '基础测试通过'
        ],
        completedItems: [
          '用户界面设计完成'
        ],
        pendingItems: [
          '核心功能开发完成',
          '基础测试通过'
        ],
        riskLevel: 'high',
        recommendations: [
          '增加开发资源',
          '调整里程碑时间',
          '优先完成核心功能'
        ]
      }
    }
  ];

  useEffect(() => {
    // 模拟API调用
    const fetchAlert = () => {
      const foundAlert = mockAlerts.find(a => a.id === parseInt(id));
      if (foundAlert) {
        setAlert(foundAlert);
      }
      setLoading(false);
    };

    setTimeout(fetchAlert, 500); // 模拟网络延迟
  }, [id]);

  const getAlertIcon = (type) => {
    switch (type) {
      case 'task_overdue':
        return <Clock className="w-6 h-6" />;
      case 'project_delay':
        return <Target className="w-6 h-6" />;
      case 'workload_overload':
        return <AlertTriangle className="w-6 h-6" />;
      case 'milestone_risk':
        return <AlertTriangle className="w-6 h-6" />;
      default:
        return <Info className="w-6 h-6" />;
    }
  };

  const getPriorityColor = (priority) => {
    switch (priority) {
      case 'high':
        return 'text-danger';
      case 'medium':
        return 'text-warning';
      case 'low':
        return 'text-info';
      default:
        return 'text-text-muted';
    }
  };

  const getPriorityBgColor = (priority) => {
    switch (priority) {
      case 'high':
        return 'bg-danger/10 text-danger';
      case 'medium':
        return 'bg-warning/10 text-warning';
      case 'low':
        return 'bg-info/10 text-info';
      default:
        return 'bg-text-muted/10 text-text-muted';
    }
  };

  const getTimeAgo = (date) => {
    const now = new Date();
    const diff = now - date;
    const minutes = Math.floor(diff / (1000 * 60));
    const hours = Math.floor(diff / (1000 * 60 * 60));
    const days = Math.floor(diff / (1000 * 60 * 60 * 24));

    if (minutes < 60) {
      return `${minutes}分钟前`;
    } else if (hours < 24) {
      return `${hours}小时前`;
    } else {
      return `${days}天前`;
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-bg-primary flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto mb-4"></div>
          <p className="text-text-secondary">加载中...</p>
        </div>
      </div>
    );
  }

  if (!alert) {
    return (
      <div className="min-h-screen bg-bg-primary flex items-center justify-center">
        <div className="text-center">
          <X className="w-16 h-16 text-text-muted mx-auto mb-4" />
          <h2 className="text-2xl font-semibold text-text-primary mb-2">消息不存在</h2>
          <p className="text-text-secondary mb-6">该消息可能已被删除或不存在</p>
          <button 
            onClick={() => navigate('/')}
            className="btn btn-primary"
          >
            返回首页
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-bg-primary">
      {/* 页面头部 */}
      <div className="bg-bg-card border-b border-border">
        <div className="container mx-auto px-6 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <button 
                onClick={() => navigate(-1)}
                className="p-2 hover:bg-bg-tertiary rounded-lg transition-colors"
              >
                <ArrowLeft className="w-5 h-5" />
              </button>
              <div className="flex items-center space-x-3">
                <div className={`p-3 rounded-lg ${getPriorityBgColor(alert.priority)}`}>
                  {getAlertIcon(alert.type)}
                </div>
                <div>
                  <h1 className="text-2xl font-bold text-text-primary">{alert.title}</h1>
                  <p className="text-text-secondary">{getTimeAgo(alert.createdAt)}</p>
                </div>
              </div>
            </div>
            <div className="flex items-center space-x-3">
              <span className={`px-3 py-1 rounded-full text-sm font-medium ${getPriorityBgColor(alert.priority)}`}>
                {alert.priority === 'high' ? '高优先级' : alert.priority === 'medium' ? '中优先级' : '低优先级'}
              </span>
              <button className="btn btn-secondary">
                <ExternalLink className="w-4 h-4" />
                查看相关
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* 页面内容 */}
      <div className="container mx-auto px-6 py-8">
        <motion.div 
          className="space-y-8"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          {/* 预警概览 */}
          <div className="card">
            <div className="text-center mb-6">
              <h2 className="text-2xl font-semibold text-text-primary">预警概览</h2>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div className="space-y-4">
                <div>
                  <h3 className="text-lg font-medium text-text-primary mb-3">预警详情</h3>
                  <p className="text-text-secondary leading-relaxed text-lg">{alert.message}</p>
                </div>
                
                <div className="space-y-3">
                  <div className="flex items-center space-x-2">
                    <span className="text-sm font-medium text-text-secondary">预警类型:</span>
                    <span className="text-sm text-text-primary">
                      {alert.type === 'task_overdue' && '任务延期'}
                      {alert.type === 'project_delay' && '项目延期'}
                      {alert.type === 'workload_overload' && '工作量超载'}
                      {alert.type === 'milestone_risk' && '里程碑风险'}
                    </span>
                  </div>
                  <div className="flex items-center space-x-2">
                    <span className="text-sm font-medium text-text-secondary">目标类型:</span>
                    <span className="text-sm text-text-primary">
                      {alert.target === 'task' && '任务'}
                      {alert.target === 'project' && '项目'}
                      {alert.target === 'user' && '用户'}
                      {alert.target === 'milestone' && '里程碑'}
                    </span>
                  </div>
                </div>
              </div>

              <div className="space-y-4">
                <h3 className="text-lg font-medium text-text-primary">详细信息</h3>
                {alert.details && (
                  <div className="space-y-3">
                    {alert.type === 'task_overdue' && (
                      <>
                        <div className="flex items-center space-x-2">
                          <span className="text-sm font-medium text-text-secondary">任务名称:</span>
                          <span className="text-sm text-text-primary">{alert.details.taskName}</span>
                        </div>
                        <div className="flex items-center space-x-2">
                          <span className="text-sm font-medium text-text-secondary">所属项目:</span>
                          <span className="text-sm text-text-primary">{alert.details.projectName}</span>
                        </div>
                        <div className="flex items-center space-x-2">
                          <span className="text-sm font-medium text-text-secondary">负责人:</span>
                          <span className="text-sm text-text-primary">{alert.details.assignee}</span>
                        </div>
                        <div className="flex items-center space-x-2">
                          <span className="text-sm font-medium text-text-secondary">截止日期:</span>
                          <span className="text-sm text-text-primary">{alert.details.dueDate}</span>
                        </div>
                        <div className="flex items-center space-x-2">
                          <span className="text-sm font-medium text-text-secondary">当前进度:</span>
                          <span className="text-sm text-text-primary">{alert.details.currentProgress}%</span>
                        </div>
                        <div className="flex items-center space-x-2">
                          <span className="text-sm font-medium text-text-secondary">预期进度:</span>
                          <span className="text-sm text-text-primary">{alert.details.expectedProgress}%</span>
                        </div>
                      </>
                    )}
                    
                    {alert.type === 'project_delay' && (
                      <>
                        <div className="flex items-center space-x-2">
                          <span className="text-sm font-medium text-text-secondary">项目名称:</span>
                          <span className="text-sm text-text-primary">{alert.details.projectName}</span>
                        </div>
                        <div className="flex items-center space-x-2">
                          <span className="text-sm font-medium text-text-secondary">所属部门:</span>
                          <span className="text-sm text-text-primary">{alert.details.department}</span>
                        </div>
                        <div className="flex items-center space-x-2">
                          <span className="text-sm font-medium text-text-secondary">项目经理:</span>
                          <span className="text-sm text-text-primary">{alert.details.manager}</span>
                        </div>
                        <div className="flex items-center space-x-2">
                          <span className="text-sm font-medium text-text-secondary">当前进度:</span>
                          <span className="text-sm text-text-primary">{alert.details.currentProgress}%</span>
                        </div>
                        <div className="flex items-center space-x-2">
                          <span className="text-sm font-medium text-text-secondary">预期进度:</span>
                          <span className="text-sm text-text-primary">{alert.details.expectedProgress}%</span>
                        </div>
                      </>
                    )}

                    {alert.type === 'workload_overload' && (
                      <>
                        <div className="flex items-center space-x-2">
                          <span className="text-sm font-medium text-text-secondary">用户姓名:</span>
                          <span className="text-sm text-text-primary">{alert.details.userName}</span>
                        </div>
                        <div className="flex items-center space-x-2">
                          <span className="text-sm font-medium text-text-secondary">所属部门:</span>
                          <span className="text-sm text-text-primary">{alert.details.department}</span>
                        </div>
                        <div className="flex items-center space-x-2">
                          <span className="text-sm font-medium text-text-secondary">当前任务数:</span>
                          <span className="text-sm text-text-primary">{alert.details.currentTasks}</span>
                        </div>
                        <div className="flex items-center space-x-2">
                          <span className="text-sm font-medium text-text-secondary">建议任务数:</span>
                          <span className="text-sm text-text-primary">{alert.details.maxRecommendedTasks}</span>
                        </div>
                        <div className="flex items-center space-x-2">
                          <span className="text-sm font-medium text-text-secondary">工作量评分:</span>
                          <span className="text-sm text-text-primary">{alert.details.workloadScore}/100</span>
                        </div>
                      </>
                    )}

                    {alert.type === 'milestone_risk' && (
                      <>
                        <div className="flex items-center space-x-2">
                          <span className="text-sm font-medium text-text-secondary">里程碑名称:</span>
                          <span className="text-sm text-text-primary">{alert.details.milestoneName}</span>
                        </div>
                        <div className="flex items-center space-x-2">
                          <span className="text-sm font-medium text-text-secondary">所属项目:</span>
                          <span className="text-sm text-text-primary">{alert.details.projectName}</span>
                        </div>
                        <div className="flex items-center space-x-2">
                          <span className="text-sm font-medium text-text-secondary">截止日期:</span>
                          <span className="text-sm text-text-primary">{alert.details.dueDate}</span>
                        </div>
                        <div className="flex items-center space-x-2">
                          <span className="text-sm font-medium text-text-secondary">完成率:</span>
                          <span className="text-sm text-text-primary">{alert.details.completionRate}%</span>
                        </div>
                        <div className="flex items-center space-x-2">
                          <span className="text-sm font-medium text-text-secondary">要求完成率:</span>
                          <span className="text-sm text-text-primary">{alert.details.requiredRate}%</span>
                        </div>
                      </>
                    )}
                  </div>
                )}
              </div>
            </div>
          </div>

          {/* 详细描述和建议 */}
          {alert.details && (
            <div className="space-y-6">
              {alert.details.description && (
                <div className="card">
                  <div className="text-center mb-6">
                    <h3 className="text-2xl font-semibold text-text-primary">详细描述</h3>
                  </div>
                  <p className="text-text-secondary leading-relaxed text-lg">{alert.details.description}</p>
                </div>
              )}

              {alert.details.recommendations && (
                <div className="card">
                  <div className="text-center mb-6">
                    <h3 className="text-2xl font-semibold text-text-primary">AI 智能建议</h3>
                    <p className="text-text-secondary text-sm mt-1">基于当前数据分析，系统为您提供以下优化建议</p>
                  </div>
                  <div className="space-y-4">
                    {alert.details.recommendations.map((rec, index) => (
                      <div key={index} className="flex items-start space-x-3 p-4 bg-bg-tertiary rounded-lg">
                        <div className="w-2 h-2 bg-primary rounded-full mt-2 flex-shrink-0"></div>
                        <span className="text-text-secondary text-lg">{rec}</span>
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {alert.details.riskFactors && (
                <div className="card">
                  <div className="text-center mb-6">
                    <h3 className="text-2xl font-semibold text-text-primary">风险因素分析</h3>
                  </div>
                  <div className="space-y-4">
                    {alert.details.riskFactors.map((factor, index) => (
                      <div key={index} className="flex items-start space-x-3 p-4 bg-bg-tertiary rounded-lg">
                        <div className="w-2 h-2 bg-warning rounded-full mt-2 flex-shrink-0"></div>
                        <span className="text-text-secondary text-lg">{factor}</span>
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {alert.details.taskList && (
                <div className="card">
                  <div className="text-center mb-6">
                    <h3 className="text-2xl font-semibold text-text-primary">当前任务列表</h3>
                  </div>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    {alert.details.taskList.map((task, index) => (
                      <div key={index} className="flex items-center space-x-3 p-4 bg-bg-tertiary rounded-lg">
                        <div className="w-2 h-2 bg-primary rounded-full"></div>
                        <span className="text-text-secondary text-lg">{task}</span>
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {alert.details.deliverables && (
                <div className="card">
                  <div className="text-center mb-6">
                    <h3 className="text-2xl font-semibold text-text-primary">交付物状态</h3>
                  </div>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div>
                      <h4 className="text-lg font-medium text-success mb-4 text-center">已完成</h4>
                      <div className="space-y-3">
                        {alert.details.completedItems?.map((item, index) => (
                          <div key={index} className="flex items-center space-x-3 p-4 bg-bg-tertiary rounded-lg">
                            <CheckCircle className="w-5 h-5 text-success" />
                            <span className="text-text-secondary text-lg">{item}</span>
                          </div>
                        ))}
                      </div>
                    </div>
                    <div>
                      <h4 className="text-lg font-medium text-warning mb-4 text-center">待完成</h4>
                      <div className="space-y-3">
                        {alert.details.pendingItems?.map((item, index) => (
                          <div key={index} className="flex items-center space-x-3 p-4 bg-bg-tertiary rounded-lg">
                            <Clock className="w-5 h-5 text-warning" />
                            <span className="text-text-secondary text-lg">{item}</span>
                          </div>
                        ))}
                      </div>
                    </div>
                  </div>
                </div>
              )}
            </div>
          )}

          {/* 操作区域 */}
          <div className="card bg-gradient-to-r from-primary/5 to-secondary/5 border-primary/20">
            <div className="text-center">
              <div className="flex items-center justify-center space-x-3 mb-6">
                <div className="w-3 h-3 bg-success rounded-full animate-pulse"></div>
                <h3 className="text-xl font-medium text-text-primary">AI 智能分析系统</h3>
              </div>
              <p className="text-text-secondary mb-6">基于实时数据分析，为您提供精准的预警信息和处理建议</p>
              <div className="flex items-center justify-center space-x-4">
                <button className="btn btn-secondary">
                  <ExternalLink className="w-4 h-4" />
                  查看相关项目
                </button>
                <button className="btn btn-primary">
                  <Bell className="w-4 h-4" />
                  处理预警
                </button>
              </div>
            </div>
          </div>
        </motion.div>
      </div>
    </div>
  );
};

export default MessageDetail;
