import React from 'react';
import { TrendingUp, TrendingDown } from 'lucide-react';

const StatCard = ({ title, value, change, changeType, icon: Icon, color }) => {
  const getColorClasses = (color) => {
    switch (color) {
      case 'primary':
        return {
          icon: 'text-primary',
          bg: 'bg-primary/10',
          border: 'border-primary/20'
        };
      case 'success':
        return {
          icon: 'text-success',
          bg: 'bg-success/10',
          border: 'border-success/20'
        };
      case 'warning':
        return {
          icon: 'text-warning',
          bg: 'bg-warning/10',
          border: 'border-warning/20'
        };
      case 'danger':
        return {
          icon: 'text-danger',
          bg: 'bg-danger/10',
          border: 'border-danger/20'
        };
      case 'info':
        return {
          icon: 'text-info',
          bg: 'bg-info/10',
          border: 'border-info/20'
        };
      default:
        return {
          icon: 'text-primary',
          bg: 'bg-primary/10',
          border: 'border-primary/20'
        };
    }
  };

  const colorClasses = getColorClasses(color);

  return (
    <div className="stat-card group">
      <div className="flex items-center justify-between">
        <div className="flex-1">
          <div className="flex items-center space-x-3 mb-3">
            <div className={`p-3 rounded-xl ${colorClasses.bg} ${colorClasses.border} group-hover:scale-110 transition-transform duration-300`}>
              <Icon className={`w-6 h-6 ${colorClasses.icon}`} />
            </div>
            <div className="flex-1">
              <h3 className="text-sm font-medium text-text-secondary mb-1">{title}</h3>
              <div className="text-3xl font-bold text-text-primary">{value}</div>
            </div>
          </div>
          
          {change && (
            <div className="flex items-center space-x-1">
              {changeType === 'positive' ? (
                <TrendingUp className="w-4 h-4 text-success" />
              ) : (
                <TrendingDown className="w-4 h-4 text-danger" />
              )}
              <span className={`text-sm font-medium ${
                changeType === 'positive' ? 'text-success' : 'text-danger'
              }`}>
                {change}
              </span>
              <span className="text-xs text-text-secondary">vs 上月</span>
            </div>
          )}
        </div>
      </div>
      
      {/* 装饰性背景元素 */}
      <div className="absolute top-0 right-0 w-20 h-20 opacity-5 group-hover:opacity-10 transition-opacity duration-300">
        <Icon className="w-full h-full" />
      </div>
    </div>
  );
};

export default StatCard;
