import React, { useState } from 'react';
import { 
  Plus, 
  Filter, 
  Search, 
  MoreVertical,
  Edit,
  Trash2,
  Eye,
  Calendar,
  User,
  Target,
  Clock,
  CheckCircle,
  AlertTriangle,
  BarChart3
} from 'lucide-react';
import TaskList from '../components/TaskList';

const Tasks = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');
  const [priorityFilter, setPriorityFilter] = useState('all');
  const [projectFilter, setProjectFilter] = useState('all');
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [viewMode, setViewMode] = useState('list'); // 'list' or 'gantt'

  const tasks = [
    {
      id: 1,
      title: '完成用户界面设计',
      description: '设计移动端应用的用户界面，包括登录、主页、设置等页面',
      project: '移动端应用开发',
      assignee: '张三',
      status: 'in-progress',
      priority: 'high',
      type: 'design',
      startDate: '2024-01-15',
      dueDate: '2024-01-20',
      progress: 75,
      estimatedHours: 40,
      actualHours: 30,
      dependencies: [],
      tags: ['UI设计', '移动端']
    },
    {
      id: 2,
      title: '数据库架构设计',
      description: '设计数据分析平台的数据库架构，包括表结构、索引、关系等',
      project: '数据分析平台',
      assignee: '李四',
      status: 'pending',
      priority: 'high',
      type: 'development',
      startDate: '2024-01-22',
      dueDate: '2024-01-25',
      progress: 0,
      estimatedHours: 32,
      actualHours: 0,
      dependencies: [1],
      tags: ['数据库', '架构设计']
    },
    {
      id: 3,
      title: 'API接口开发',
      description: '开发移动端应用的后端API接口，包括用户认证、数据获取等',
      project: '移动端应用开发',
      assignee: '王五',
      status: 'in-progress',
      priority: 'medium',
      type: 'development',
      startDate: '2024-01-18',
      dueDate: '2024-01-25',
      progress: 60,
      estimatedHours: 48,
      actualHours: 28,
      dependencies: [1],
      tags: ['API', '后端开发']
    },
    {
      id: 4,
      title: '前端页面开发',
      description: '基于设计稿开发前端页面，实现用户交互功能',
      project: '企业官网重构',
      assignee: '赵六',
      status: 'review',
      priority: 'medium',
      type: 'development',
      startDate: '2024-01-10',
      dueDate: '2024-01-18',
      progress: 90,
      estimatedHours: 56,
      actualHours: 50,
      dependencies: [],
      tags: ['前端', '页面开发']
    },
    {
      id: 5,
      title: '系统测试',
      description: '对数据分析平台进行全面测试，包括功能测试、性能测试等',
      project: '数据分析平台',
      assignee: '钱七',
      status: 'completed',
      priority: 'low',
      type: 'testing',
      startDate: '2024-01-15',
      dueDate: '2024-01-20',
      progress: 100,
      estimatedHours: 24,
      actualHours: 22,
      dependencies: [2, 3],
      tags: ['测试', '质量保证']
    }
  ];

  const projects = ['移动端应用开发', '数据分析平台', '企业官网重构'];

  const filteredTasks = tasks.filter(task => {
    const matchesSearch = task.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         task.description.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStatus = statusFilter === 'all' || task.status === statusFilter;
    const matchesPriority = priorityFilter === 'all' || task.priority === priorityFilter;
    const matchesProject = projectFilter === 'all' || task.project === projectFilter;
    
    return matchesSearch && matchesStatus && matchesPriority && matchesProject;
  });

  const getStatusColor = (status) => {
    switch (status) {
      case 'pending': return 'text-text-muted';
      case 'in-progress': return 'text-primary';
      case 'review': return 'text-warning';
      case 'completed': return 'text-success';
      case 'paused': return 'text-danger';
      default: return 'text-text-muted';
    }
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'pending': return Clock;
      case 'in-progress': return Target;
      case 'review': return AlertTriangle;
      case 'completed': return CheckCircle;
      case 'paused': return AlertTriangle;
      default: return Clock;
    }
  };

  const getPriorityColor = (priority) => {
    switch (priority) {
      case 'high': return 'text-danger';
      case 'medium': return 'text-warning';
      case 'low': return 'text-success';
      default: return 'text-text-muted';
    }
  };

  const getTypeColor = (type) => {
    switch (type) {
      case 'development': return 'bg-primary/10 text-primary';
      case 'design': return 'bg-secondary/10 text-secondary';
      case 'testing': return 'bg-success/10 text-success';
      case 'documentation': return 'bg-warning/10 text-warning';
      default: return 'bg-text-muted/10 text-text-muted';
    }
  };

  const getStatusText = (status) => {
    switch (status) {
      case 'pending': return '待开始';
      case 'in-progress': return '进行中';
      case 'review': return '待验收';
      case 'completed': return '已完成';
      case 'paused': return '已暂停';
      default: return '未知';
    }
  };

  return (
    <div className="space-y-6">
      {/* 页面标题 */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-text-primary">任务管理</h1>
          <p className="text-text-secondary mt-1">管理项目任务，跟踪进度和状态</p>
        </div>
        <div className="flex items-center space-x-3">
          <div className="flex items-center space-x-2 bg-bg-tertiary rounded-lg p-1">
            <button
              className={`px-3 py-1 rounded text-sm font-medium transition-colors ${
                viewMode === 'list' 
                  ? 'bg-bg-card text-text-primary' 
                  : 'text-text-secondary hover:text-text-primary'
              }`}
              onClick={() => setViewMode('list')}
            >
              列表视图
            </button>
            <button
              className={`px-3 py-1 rounded text-sm font-medium transition-colors ${
                viewMode === 'gantt' 
                  ? 'bg-bg-card text-text-primary' 
                  : 'text-text-secondary hover:text-text-primary'
              }`}
              onClick={() => setViewMode('gantt')}
            >
              甘特图
            </button>
          </div>
          <button 
            className="btn btn-primary flex items-center space-x-2"
            onClick={() => setShowCreateModal(true)}
          >
            <Plus className="w-4 h-4" />
            <span>新建任务</span>
          </button>
        </div>
      </div>

      {/* 筛选和搜索 */}
      <div className="card">
        <div className="flex flex-col lg:flex-row lg:items-center justify-between space-y-4 lg:space-y-0">
          {/* 搜索框 */}
          <div className="flex items-center space-x-2 bg-bg-tertiary rounded-lg px-3 py-2 flex-1 max-w-md">
            <Search className="w-4 h-4 text-text-muted" />
            <input
              type="text"
              placeholder="搜索任务名称或描述..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="bg-transparent border-none outline-none text-sm text-text-primary placeholder-text-muted flex-1"
            />
          </div>

          {/* 筛选器 */}
          <div className="flex items-center space-x-4">
            <div className="flex items-center space-x-2">
              <Filter className="w-4 h-4 text-text-muted" />
              <select
                value={statusFilter}
                onChange={(e) => setStatusFilter(e.target.value)}
                className="bg-bg-tertiary border border-border rounded-lg px-3 py-2 text-sm text-text-primary"
              >
                <option value="all">所有状态</option>
                <option value="pending">待开始</option>
                <option value="in-progress">进行中</option>
                <option value="review">待验收</option>
                <option value="completed">已完成</option>
                <option value="paused">已暂停</option>
              </select>
            </div>

            <div className="flex items-center space-x-2">
              <Target className="w-4 h-4 text-text-muted" />
              <select
                value={priorityFilter}
                onChange={(e) => setPriorityFilter(e.target.value)}
                className="bg-bg-tertiary border border-border rounded-lg px-3 py-2 text-sm text-text-primary"
              >
                <option value="all">所有优先级</option>
                <option value="high">高优先级</option>
                <option value="medium">中优先级</option>
                <option value="low">低优先级</option>
              </select>
            </div>

            <div className="flex items-center space-x-2">
              <BarChart3 className="w-4 h-4 text-text-muted" />
              <select
                value={projectFilter}
                onChange={(e) => setProjectFilter(e.target.value)}
                className="bg-bg-tertiary border border-border rounded-lg px-3 py-2 text-sm text-text-primary"
              >
                <option value="all">所有项目</option>
                {projects.map(project => (
                  <option key={project} value={project}>{project}</option>
                ))}
              </select>
            </div>
          </div>
        </div>
      </div>

      {/* 任务统计 */}
      <div className="grid grid-cols-1 md:grid-cols-5 gap-4">
        <div className="card text-center">
          <div className="text-2xl font-bold text-text-primary">{tasks.length}</div>
          <div className="text-sm text-text-secondary">总任务数</div>
        </div>
        <div className="card text-center">
          <div className="text-2xl font-bold text-primary">
            {tasks.filter(t => t.status === 'in-progress').length}
          </div>
          <div className="text-sm text-text-secondary">进行中</div>
        </div>
        <div className="card text-center">
          <div className="text-2xl font-bold text-warning">
            {tasks.filter(t => t.status === 'review').length}
          </div>
          <div className="text-sm text-text-secondary">待验收</div>
        </div>
        <div className="card text-center">
          <div className="text-2xl font-bold text-success">
            {tasks.filter(t => t.status === 'completed').length}
          </div>
          <div className="text-sm text-text-secondary">已完成</div>
        </div>
        <div className="card text-center">
          <div className="text-2xl font-bold text-danger">
            {tasks.filter(t => t.priority === 'high' && t.status !== 'completed').length}
          </div>
          <div className="text-sm text-text-secondary">紧急任务</div>
        </div>
      </div>

      {/* 任务列表 */}
      {viewMode === 'list' && (
        <div className="card">
          <div className="flex items-center justify-between mb-6">
            <h2 className="text-lg font-semibold">任务列表</h2>
            <span className="text-sm text-text-muted">
              共 {filteredTasks.length} 个任务
            </span>
          </div>

          <div className="space-y-4">
            {filteredTasks.map((task) => {
              const StatusIcon = getStatusIcon(task.status);
              return (
                <div key={task.id} className="border border-border rounded-lg p-4 hover:bg-bg-tertiary transition-colors">
                  <div className="flex items-start justify-between">
                    <div className="flex-1">
                      <div className="flex items-center space-x-3 mb-2">
                        <StatusIcon className={`w-4 h-4 ${getStatusColor(task.status)}`} />
                        <h3 className="font-medium text-text-primary">{task.title}</h3>
                        <span className={`px-2 py-1 rounded-full text-xs font-medium ${getTypeColor(task.type)}`}>
                          {task.type === 'development' && '开发'}
                          {task.type === 'design' && '设计'}
                          {task.type === 'testing' && '测试'}
                          {task.type === 'documentation' && '文档'}
                        </span>
                        <span className={`px-2 py-1 rounded-full text-xs font-medium ${getPriorityColor(task.priority)}`}>
                          {task.priority === 'high' && '高优先级'}
                          {task.priority === 'medium' && '中优先级'}
                          {task.priority === 'low' && '低优先级'}
                        </span>
                      </div>
                      
                      <p className="text-sm text-text-secondary mb-3">{task.description}</p>
                      
                      <div className="flex items-center space-x-6 text-sm text-text-muted mb-3">
                        <div className="flex items-center space-x-1">
                          <BarChart3 className="w-3 h-3" />
                          <span>{task.project}</span>
                        </div>
                        <div className="flex items-center space-x-1">
                          <User className="w-3 h-3" />
                          <span>{task.assignee}</span>
                        </div>
                        <div className="flex items-center space-x-1">
                          <Calendar className="w-3 h-3" />
                          <span>{task.dueDate}</span>
                        </div>
                        <div className="flex items-center space-x-1">
                          <Clock className="w-3 h-3" />
                          <span>{task.actualHours}/{task.estimatedHours}h</span>
                        </div>
                      </div>

                      {/* 进度条 */}
                      <div className="mb-3">
                        <div className="flex items-center justify-between text-sm mb-1">
                          <span className="text-text-secondary">进度</span>
                          <span className="text-text-primary font-medium">{task.progress}%</span>
                        </div>
                        <div className="w-full bg-bg-tertiary rounded-full h-2">
                          <div 
                            className="bg-gradient-to-r from-primary to-secondary h-2 rounded-full transition-all duration-500"
                            style={{ width: `${task.progress}%` }}
                          ></div>
                        </div>
                      </div>

                      {/* 标签 */}
                      <div className="flex flex-wrap gap-1">
                        {task.tags.map((tag, index) => (
                          <span key={index} className="px-2 py-1 bg-bg-tertiary text-xs rounded-full text-text-muted">
                            {tag}
                          </span>
                        ))}
                      </div>
                    </div>

                    <div className="flex items-center space-x-2 ml-4">
                      <button className="p-1 hover:bg-bg-tertiary rounded">
                        <Eye className="w-4 h-4 text-text-muted" />
                      </button>
                      <button className="p-1 hover:bg-bg-tertiary rounded">
                        <Edit className="w-4 h-4 text-text-muted" />
                      </button>
                      <button className="p-1 hover:bg-bg-tertiary rounded">
                        <MoreVertical className="w-4 h-4 text-text-muted" />
                      </button>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      )}

      {/* 甘特图视图 */}
      {viewMode === 'gantt' && (
        <div className="card">
          <div className="flex items-center justify-between mb-6">
            <h2 className="text-lg font-semibold">甘特图视图</h2>
            <div className="flex items-center space-x-2 text-sm text-text-muted">
              <div className="w-3 h-3 bg-primary rounded"></div>
              <span>进行中</span>
              <div className="w-3 h-3 bg-success rounded"></div>
              <span>已完成</span>
              <div className="w-3 h-3 bg-warning rounded"></div>
              <span>待验收</span>
            </div>
          </div>
          
          <div className="overflow-x-auto">
            <div className="min-w-[800px]">
              {/* 时间轴 */}
              <div className="flex border-b border-border mb-4">
                <div className="w-48 font-medium text-text-secondary py-2">任务</div>
                <div className="flex-1 grid grid-cols-7 gap-1">
                  {['1月15日', '1月16日', '1月17日', '1月18日', '1月19日', '1月20日', '1月21日'].map((date, index) => (
                    <div key={index} className="text-center text-xs text-text-muted py-2">
                      {date}
                    </div>
                  ))}
                </div>
              </div>

              {/* 任务条 */}
              {filteredTasks.map((task) => (
                <div key={task.id} className="flex items-center border-b border-border py-2">
                  <div className="w-48 pr-4">
                    <div className="font-medium text-sm">{task.title}</div>
                    <div className="text-xs text-text-secondary">{task.assignee}</div>
                  </div>
                  <div className="flex-1 relative h-8">
                    <div 
                      className={`absolute h-6 rounded-lg transition-all duration-300 ${
                        task.status === 'completed' ? 'bg-success' :
                        task.status === 'in-progress' ? 'bg-primary' :
                        task.status === 'review' ? 'bg-warning' :
                        'bg-text-muted'
                      }`}
                      style={{
                        left: `${(new Date(task.startDate).getDate() - 15) * 14.28}%`,
                        width: `${Math.max(14.28, (new Date(task.dueDate) - new Date(task.startDate)) / (1000 * 60 * 60 * 24) * 14.28)}%`
                      }}
                    >
                      <div className="flex items-center justify-center h-full text-xs text-white font-medium">
                        {task.progress}%
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      )}

      {/* 创建任务模态框 */}
      {showCreateModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-bg-card rounded-lg p-6 w-full max-w-2xl mx-4">
            <div className="flex items-center justify-between mb-6">
              <h2 className="text-xl font-semibold">创建新任务</h2>
              <button 
                onClick={() => setShowCreateModal(false)}
                className="p-2 hover:bg-bg-tertiary rounded-lg"
              >
                <Plus className="w-5 h-5 rotate-45" />
              </button>
            </div>
            
            <form className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium mb-2">任务名称</label>
                  <input 
                    type="text" 
                    className="w-full px-3 py-2 bg-bg-tertiary border border-border rounded-lg focus:outline-none focus:border-primary"
                    placeholder="输入任务名称"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium mb-2">所属项目</label>
                  <select className="w-full px-3 py-2 bg-bg-tertiary border border-border rounded-lg focus:outline-none focus:border-primary">
                    {projects.map(project => (
                      <option key={project}>{project}</option>
                    ))}
                  </select>
                </div>
              </div>
              
              <div>
                <label className="block text-sm font-medium mb-2">任务描述</label>
                <textarea 
                  className="w-full px-3 py-2 bg-bg-tertiary border border-border rounded-lg focus:outline-none focus:border-primary"
                  rows="3"
                  placeholder="描述任务目标和要求"
                ></textarea>
              </div>
              
              <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
                <div>
                  <label className="block text-sm font-medium mb-2">负责人</label>
                  <select className="w-full px-3 py-2 bg-bg-tertiary border border-border rounded-lg focus:outline-none focus:border-primary">
                    <option>张三</option>
                    <option>李四</option>
                    <option>王五</option>
                    <option>赵六</option>
                  </select>
                </div>
                <div>
                  <label className="block text-sm font-medium mb-2">任务类型</label>
                  <select className="w-full px-3 py-2 bg-bg-tertiary border border-border rounded-lg focus:outline-none focus:border-primary">
                    <option value="development">开发</option>
                    <option value="design">设计</option>
                    <option value="testing">测试</option>
                    <option value="documentation">文档</option>
                  </select>
                </div>
                <div>
                  <label className="block text-sm font-medium mb-2">优先级</label>
                  <select className="w-full px-3 py-2 bg-bg-tertiary border border-border rounded-lg focus:outline-none focus:border-primary">
                    <option value="high">高</option>
                    <option value="medium">中</option>
                    <option value="low">低</option>
                  </select>
                </div>
                <div>
                  <label className="block text-sm font-medium mb-2">预估工时(h)</label>
                  <input 
                    type="number" 
                    className="w-full px-3 py-2 bg-bg-tertiary border border-border rounded-lg focus:outline-none focus:border-primary"
                    placeholder="40"
                  />
                </div>
              </div>
              
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium mb-2">开始日期</label>
                  <input 
                    type="date" 
                    className="w-full px-3 py-2 bg-bg-tertiary border border-border rounded-lg focus:outline-none focus:border-primary"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium mb-2">截止日期</label>
                  <input 
                    type="date" 
                    className="w-full px-3 py-2 bg-bg-tertiary border border-border rounded-lg focus:outline-none focus:border-primary"
                  />
                </div>
              </div>
              
              <div className="flex justify-end space-x-3 pt-4">
                <button 
                  type="button"
                  onClick={() => setShowCreateModal(false)}
                  className="px-4 py-2 text-text-secondary hover:text-text-primary"
                >
                  取消
                </button>
                <button 
                  type="submit"
                  className="btn btn-primary"
                >
                  创建任务
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Tasks;
