import React, { useState, useEffect } from 'react';
import { Bell, AlertTriangle, Info, X, CheckCircle, Clock, Target, ArrowRight, ExternalLink, ArrowLeft } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';

const AlertNotification = () => {
  const [alerts, setAlerts] = useState([]);
  const [isOpen, setIsOpen] = useState(false);
  const [unreadCount, setUnreadCount] = useState(0);
  const [selectedAlert, setSelectedAlert] = useState(null);
  const [showDetail, setShowDetail] = useState(false);

  // 模拟预警数据
  const mockAlerts = [
    {
      id: 1,
      type: 'task_overdue',
      title: '任务延期预警',
      message: '任务"完成用户界面设计"将在2天后到期，当前进度75%',
      priority: 'high',
      target: 'task',
      targetId: 1,
      createdAt: new Date(Date.now() - 1000 * 60 * 30), // 30分钟前
      isRead: false,
      details: {
        taskName: '完成用户界面设计',
        projectName: '移动端应用开发',
        assignee: '张三',
        dueDate: '2024-01-22',
        currentProgress: 75,
        expectedProgress: 90,
        description: '设计并实现移动端应用的用户界面，包括登录页面、主页面、设置页面等核心功能界面。',
        recommendations: [
          '增加工作时间投入',
          '寻求设计团队协助',
          '优先完成核心功能界面'
        ]
      }
    },
    {
      id: 2,
      type: 'project_delay',
      title: '项目延期预警',
      message: '项目"移动端应用开发"进度滞后，当前进度45%，预期进度60%',
      priority: 'medium',
      target: 'project',
      targetId: 1,
      createdAt: new Date(Date.now() - 1000 * 60 * 60 * 2), // 2小时前
      isRead: false,
      details: {
        projectName: '移动端应用开发',
        department: '技术部',
        manager: '李四',
        startDate: '2024-01-01',
        endDate: '2024-03-15',
        currentProgress: 45,
        expectedProgress: 60,
        description: '开发企业移动端应用，支持iOS和Android平台，包含用户管理、数据展示、消息推送等功能。',
        riskFactors: [
          '人力资源不足',
          '技术难点较多',
          '需求变更频繁'
        ],
        recommendations: [
          '增加开发人员',
          '优化开发流程',
          '加强需求管理'
        ]
      }
    },
    {
      id: 3,
      type: 'workload_overload',
      title: '工作量超载预警',
      message: '用户"王五"当前有8个进行中任务，可能存在工作量超载',
      priority: 'medium',
      target: 'user',
      targetId: 3,
      createdAt: new Date(Date.now() - 1000 * 60 * 60 * 4), // 4小时前
      isRead: true,
      details: {
        userName: '王五',
        department: '技术部',
        position: '高级开发工程师',
        currentTasks: 8,
        maxRecommendedTasks: 5,
        taskList: [
          'API接口开发',
          '数据库设计',
          '前端页面开发',
          '单元测试编写',
          '文档编写',
          '代码审查',
          '性能优化',
          'Bug修复'
        ],
        workloadScore: 85,
        recommendations: [
          '重新分配部分任务',
          '延长任务截止时间',
          '提供技术支持协助'
        ]
      }
    },
    {
      id: 4,
      type: 'milestone_risk',
      title: '里程碑风险预警',
      message: '里程碑"第一阶段完成"可能无法按时达成，建议调整计划',
      priority: 'high',
      target: 'milestone',
      targetId: 1,
      createdAt: new Date(Date.now() - 1000 * 60 * 60 * 6), // 6小时前
      isRead: false,
      details: {
        milestoneName: '第一阶段完成',
        projectName: '移动端应用开发',
        dueDate: '2024-02-01',
        currentDate: new Date(),
        completionRate: 65,
        requiredRate: 80,
        deliverables: [
          '用户界面设计完成',
          '核心功能开发完成',
          '基础测试通过'
        ],
        completedItems: [
          '用户界面设计完成'
        ],
        pendingItems: [
          '核心功能开发完成',
          '基础测试通过'
        ],
        riskLevel: 'high',
        recommendations: [
          '增加开发资源',
          '调整里程碑时间',
          '优先完成核心功能'
        ]
      }
    }
  ];

  useEffect(() => {
    // 初始化预警数据
    setAlerts(mockAlerts);
    setUnreadCount(mockAlerts.filter(alert => !alert.isRead).length);
  }, []);

  // 定期检查新预警（模拟）
  useEffect(() => {
    const interval = setInterval(() => {
      // 模拟新预警生成
      const shouldGenerateAlert = Math.random() < 0.1; // 10%概率生成新预警
      if (shouldGenerateAlert) {
        const newAlert = {
          id: Date.now(),
          type: 'task_overdue',
          title: '任务延期预警',
          message: '任务"API接口开发"将在1天后到期，请及时处理',
          priority: 'high',
          target: 'task',
          targetId: 3,
          createdAt: new Date(),
          isRead: false,
          details: {
            taskName: 'API接口开发',
            projectName: '移动端应用开发',
            assignee: '王五',
            dueDate: '2024-01-21',
            currentProgress: 60,
            expectedProgress: 90,
            description: '开发移动端应用所需的API接口，包括用户认证、数据获取、文件上传等功能接口。',
            recommendations: [
              '加班完成开发',
              '寻求团队协助',
              '简化接口功能'
            ]
          }
        };
        setAlerts(prev => [newAlert, ...prev]);
        setUnreadCount(prev => prev + 1);
      }
    }, 30000); // 每30秒检查一次

    return () => clearInterval(interval);
  }, []);

  const markAsRead = (alertId) => {
    setAlerts(prev => 
      prev.map(alert => 
        alert.id === alertId ? { ...alert, isRead: true } : alert
      )
    );
    setUnreadCount(prev => Math.max(0, prev - 1));
  };

  const markAllAsRead = () => {
    setAlerts(prev => 
      prev.map(alert => ({ ...alert, isRead: true }))
    );
    setUnreadCount(0);
  };

  const deleteAlert = (alertId) => {
    const alert = alerts.find(a => a.id === alertId);
    if (alert && !alert.isRead) {
      setUnreadCount(prev => Math.max(0, prev - 1));
    }
    setAlerts(prev => prev.filter(alert => alert.id !== alertId));
  };

  const handleAlertClick = (alert) => {
    setSelectedAlert(alert);
    setShowDetail(true);
    if (!alert.isRead) {
      markAsRead(alert.id);
    }
  };

  const handleBackToList = () => {
    setShowDetail(false);
    setSelectedAlert(null);
  };

  const getAlertIcon = (type) => {
    switch (type) {
      case 'task_overdue':
        return <Clock className="w-4 h-4" />;
      case 'project_delay':
        return <Target className="w-4 h-4" />;
      case 'workload_overload':
        return <AlertTriangle className="w-4 h-4" />;
      case 'milestone_risk':
        return <AlertTriangle className="w-4 h-4" />;
      default:
        return <Info className="w-4 h-4" />;
    }
  };

  const getPriorityColor = (priority) => {
    switch (priority) {
      case 'high':
        return 'text-danger border-l-danger';
      case 'medium':
        return 'text-warning border-l-warning';
      case 'low':
        return 'text-info border-l-info';
      default:
        return 'text-text-muted border-l-text-muted';
    }
  };

  const getPriorityBgColor = (priority) => {
    switch (priority) {
      case 'high':
        return 'bg-danger/10 text-danger';
      case 'medium':
        return 'bg-warning/10 text-warning';
      case 'low':
        return 'bg-info/10 text-info';
      default:
        return 'bg-text-muted/10 text-text-muted';
    }
  };

  const getTimeAgo = (date) => {
    const now = new Date();
    const diff = now - date;
    const minutes = Math.floor(diff / (1000 * 60));
    const hours = Math.floor(diff / (1000 * 60 * 60));
    const days = Math.floor(diff / (1000 * 60 * 60 * 24));

    if (minutes < 60) {
      return `${minutes}分钟前`;
    } else if (hours < 24) {
      return `${hours}小时前`;
    } else {
      return `${days}天前`;
    }
  };

  const unreadAlerts = alerts.filter(alert => !alert.isRead);

  return (
    <div className="relative">
      {/* 预警按钮 */}
      <button 
        className="relative p-2 hover:bg-bg-tertiary rounded-lg transition-colors"
        onClick={() => setIsOpen(!isOpen)}
      >
        <Bell className="w-5 h-5" />
        {unreadCount > 0 && (
          <motion.span 
            className="absolute -top-1 -right-1 w-5 h-5 bg-danger text-white text-xs rounded-full flex items-center justify-center font-medium"
            initial={{ scale: 0 }}
            animate={{ scale: 1 }}
            transition={{ type: "spring", stiffness: 500, damping: 30 }}
          >
            {unreadCount > 99 ? '99+' : unreadCount}
          </motion.span>
        )}
      </button>
      
      {/* 预警面板 */}
      <AnimatePresence>
        {isOpen && (
          <motion.div 
            className="absolute right-0 mt-2 w-96 bg-bg-card border border-border rounded-lg shadow-xl z-50"
            initial={{ opacity: 0, y: -10, scale: 0.95 }}
            animate={{ opacity: 1, y: 0, scale: 1 }}
            exit={{ opacity: 0, y: -10, scale: 0.95 }}
            transition={{ duration: 0.2 }}
          >
            {/* 面板头部 */}
            <div className="flex items-center justify-between p-4 border-b border-border">
              <div className="flex items-center space-x-2">
                <AlertTriangle className="w-5 h-5 text-warning" />
                <h3 className="font-semibold text-text-primary">智能预警</h3>
                {unreadCount > 0 && (
                  <span className="px-2 py-1 bg-primary text-white text-xs rounded-full">
                    {unreadCount} 条未读
                  </span>
                )}
              </div>
              <div className="flex items-center space-x-2">
                {unreadCount > 0 && (
                  <button 
                    onClick={markAllAsRead}
                    className="text-xs text-primary hover:underline"
                  >
                    全部已读
                  </button>
                )}
                <button 
                  onClick={() => setIsOpen(false)}
                  className="p-1 hover:bg-bg-tertiary rounded"
                >
                  <X className="w-4 h-4" />
                </button>
              </div>
            </div>
            
            {/* 内容区域 */}
            <div className="max-h-96 overflow-y-auto">
              {showDetail && selectedAlert ? (
                // 消息详情视图
                <motion.div
                  initial={{ opacity: 0, x: 20 }}
                  animate={{ opacity: 1, x: 0 }}
                  exit={{ opacity: 0, x: -20 }}
                  className="p-4"
                >
                  {/* 详情头部 */}
                  <div className="flex items-center justify-between mb-4">
                    <button 
                      onClick={handleBackToList}
                      className="flex items-center space-x-2 text-sm text-primary hover:underline"
                    >
                      <ArrowLeft className="w-4 h-4" />
                      <span>返回列表</span>
                    </button>
                    <span className={`px-2 py-1 rounded-full text-xs font-medium ${getPriorityBgColor(selectedAlert.priority)}`}>
                      {selectedAlert.priority === 'high' ? '高优先级' : selectedAlert.priority === 'medium' ? '中优先级' : '低优先级'}
                    </span>
                  </div>

                  {/* 消息标题 */}
                  <div className="flex items-start space-x-3 mb-4">
                    <div className={`mt-1 ${getPriorityColor(selectedAlert.priority)}`}>
                      {getAlertIcon(selectedAlert.type)}
                    </div>
                    <div className="flex-1">
                      <h4 className="font-semibold text-text-primary mb-1">{selectedAlert.title}</h4>
                      <p className="text-sm text-text-secondary mb-2">{selectedAlert.message}</p>
                      <div className="flex items-center justify-between">
                        <span className="text-xs text-text-muted">{getTimeAgo(selectedAlert.createdAt)}</span>
                        <button 
                          onClick={(e) => {
                            e.stopPropagation();
                            deleteAlert(selectedAlert.id);
                            handleBackToList();
                          }}
                          className="text-xs text-danger hover:underline"
                        >
                          删除
                        </button>
                      </div>
                    </div>
                  </div>

                  {/* 详细信息 */}
                  {selectedAlert.details && (
                    <div className="space-y-4">
                      {/* 基本信息 */}
                      <div className="bg-bg-tertiary rounded-lg p-3">
                        <h5 className="text-sm font-medium text-text-primary mb-2">详细信息</h5>
                        <div className="space-y-2 text-xs">
                          {selectedAlert.type === 'task_overdue' && (
                            <>
                              <div className="flex justify-between">
                                <span className="text-text-secondary">任务名称:</span>
                                <span className="text-text-primary">{selectedAlert.details.taskName}</span>
                              </div>
                              <div className="flex justify-between">
                                <span className="text-text-secondary">负责人:</span>
                                <span className="text-text-primary">{selectedAlert.details.assignee}</span>
                              </div>
                              <div className="flex justify-between">
                                <span className="text-text-secondary">进度:</span>
                                <span className="text-text-primary">{selectedAlert.details.currentProgress}% / {selectedAlert.details.expectedProgress}%</span>
                              </div>
                            </>
                          )}
                          
                          {selectedAlert.type === 'project_delay' && (
                            <>
                              <div className="flex justify-between">
                                <span className="text-text-secondary">项目名称:</span>
                                <span className="text-text-primary">{selectedAlert.details.projectName}</span>
                              </div>
                              <div className="flex justify-between">
                                <span className="text-text-secondary">项目经理:</span>
                                <span className="text-text-primary">{selectedAlert.details.manager}</span>
                              </div>
                              <div className="flex justify-between">
                                <span className="text-text-secondary">进度:</span>
                                <span className="text-text-primary">{selectedAlert.details.currentProgress}% / {selectedAlert.details.expectedProgress}%</span>
                              </div>
                            </>
                          )}

                          {selectedAlert.type === 'workload_overload' && (
                            <>
                              <div className="flex justify-between">
                                <span className="text-text-secondary">用户姓名:</span>
                                <span className="text-text-primary">{selectedAlert.details.userName}</span>
                              </div>
                              <div className="flex justify-between">
                                <span className="text-text-secondary">当前任务:</span>
                                <span className="text-text-primary">{selectedAlert.details.currentTasks}个</span>
                              </div>
                              <div className="flex justify-between">
                                <span className="text-text-secondary">工作量评分:</span>
                                <span className="text-text-primary">{selectedAlert.details.workloadScore}/100</span>
                              </div>
                            </>
                          )}

                          {selectedAlert.type === 'milestone_risk' && (
                            <>
                              <div className="flex justify-between">
                                <span className="text-text-secondary">里程碑:</span>
                                <span className="text-text-primary">{selectedAlert.details.milestoneName}</span>
                              </div>
                              <div className="flex justify-between">
                                <span className="text-text-secondary">完成率:</span>
                                <span className="text-text-primary">{selectedAlert.details.completionRate}% / {selectedAlert.details.requiredRate}%</span>
                              </div>
                            </>
                          )}
                        </div>
                      </div>

                      {/* AI 建议 */}
                      {selectedAlert.details.recommendations && (
                        <div className="bg-bg-tertiary rounded-lg p-3">
                          <h5 className="text-sm font-medium text-text-primary mb-2">AI 建议</h5>
                          <div className="space-y-1">
                            {selectedAlert.details.recommendations.slice(0, 2).map((rec, index) => (
                              <div key={index} className="flex items-start space-x-2">
                                <div className="w-1 h-1 bg-primary rounded-full mt-1.5 flex-shrink-0"></div>
                                <span className="text-xs text-text-secondary">{rec}</span>
                              </div>
                            ))}
                            {selectedAlert.details.recommendations.length > 2 && (
                              <div className="text-xs text-primary mt-2">还有 {selectedAlert.details.recommendations.length - 2} 条建议...</div>
                            )}
                          </div>
                        </div>
                      )}

                      {/* 操作按钮 */}
                      <div className="flex space-x-2">
                        <button className="flex-1 btn btn-secondary text-xs py-2">
                          <ExternalLink className="w-3 h-3" />
                          查看详情
                        </button>
                        <button className="flex-1 btn btn-primary text-xs py-2">
                          处理预警
                        </button>
                      </div>
                    </div>
                  )}
                </motion.div>
              ) : (
                // 消息列表视图
                <motion.div
                  initial={{ opacity: 0, x: -20 }}
                  animate={{ opacity: 1, x: 0 }}
                  exit={{ opacity: 0, x: 20 }}
                >
                  {alerts.length === 0 ? (
                    <div className="p-8 text-center">
                      <CheckCircle className="w-12 h-12 text-success mx-auto mb-3" />
                      <p className="text-text-secondary">暂无预警信息</p>
                    </div>
                  ) : (
                    <div className="divide-y divide-border">
                      {alerts.map((alert) => (
                        <motion.div
                          key={alert.id}
                          className={`p-4 hover:bg-bg-tertiary transition-colors border-l-4 cursor-pointer ${
                            alert.isRead ? 'opacity-60' : ''
                          } ${getPriorityColor(alert.priority)}`}
                          initial={{ opacity: 0, x: -20 }}
                          animate={{ opacity: 1, x: 0 }}
                          exit={{ opacity: 0, x: -20 }}
                          onClick={() => handleAlertClick(alert)}
                        >
                          <div className="flex items-start space-x-3">
                            <div className={`mt-1 ${getPriorityColor(alert.priority)}`}>
                              {getAlertIcon(alert.type)}
                            </div>
                            <div className="flex-1 min-w-0">
                              <div className="flex items-center justify-between mb-1">
                                <h4 className="font-medium text-text-primary text-sm">
                                  {alert.title}
                                </h4>
                                <div className="flex items-center space-x-1">
                                  {!alert.isRead && (
                                    <div className="w-2 h-2 bg-primary rounded-full"></div>
                                  )}
                                  <span className="text-xs text-text-muted">
                                    {getTimeAgo(alert.createdAt)}
                                  </span>
                                </div>
                              </div>
                              <p className="text-sm text-text-secondary mb-2">
                                {alert.message}
                              </p>
                              <div className="flex items-center space-x-2">
                                <span className={`px-2 py-1 rounded-full text-xs font-medium ${getPriorityBgColor(alert.priority)}`}>
                                  {alert.priority === 'high' ? '高优先级' : alert.priority === 'medium' ? '中优先级' : '低优先级'}
                                </span>
                                <button 
                                  onClick={(e) => {
                                    e.stopPropagation();
                                    deleteAlert(alert.id);
                                  }}
                                  className="text-xs text-danger hover:underline"
                                >
                                  删除
                                </button>
                              </div>
                            </div>
                            <ArrowRight className="w-4 h-4 text-text-muted" />
                          </div>
                        </motion.div>
                      ))}
                    </div>
                  )}
                </motion.div>
              )}
            </div>
            
            {/* 面板底部 */}
            <div className="p-3 border-t border-border bg-bg-tertiary">
              <div className="flex items-center justify-between text-xs text-text-muted">
                <span>AI 智能监控系统</span>
                <span>实时更新</span>
              </div>
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default AlertNotification;
